import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: "InfoDetail",
  components: {},

  data() {
    return {
      username: '',
      bloglist: [],
      name: '',
      identity: '',
      info: '',
      optional: '',
      avatar: '',
      publicPath: process.env.BASE_URL
    };
  },

  mounted() {
    this.username = this.$route.params.username;
    this.showUserInfo();
  },

  methods: {
    showUserInfo() {
      axios.post('http://127.0.0.1:8000/json/showUserInfo/', {
        name: this.$route.params.username
      }).then(response => {
        console.log(response.data);

        if (response.data['status'] != 1) {
          alert('Sorry but you are not login');
          this.$router.push('/');
        } else {
          this.bloglist = response.data['content'];
          this.name = this.bloglist[0].name;
          this.identity = this.bloglist[0].identity;
          this.info = this.bloglist[0].info;
          this.optional = this.bloglist[0].optional;
          this.avatar = this.publicPath + "static/avatars/" + this.name + '/' + this.bloglist[0].avatar;
        }
      });
    }

  }
};
