import "core-js/modules/es.array.push.js";
import NaviBox from '@/components/NaviBox.vue';
import AbstractDis from '@/components/AbstractDis.vue';
import axios from "axios";
import '@element-plus/icons-vue'; // ensure csrf-token append to post requests
// https://www.freesion.com/article/8944598323/

axios.interceptors.request.use(config => {
  config.headers['X-Requested-With'] = 'XMLHttpRequest';
  let regex = /.*csrftoken=([^;.]*).*$/; // 用于从cookie中匹配 csrftoken值

  config.headers['X-CSRFToken'] = document.cookie.match(regex) === null ? null : document.cookie.match(regex)[1];
  return config;
});
export default {
  components: {
    NaviBox,
    AbstractDis
  },
  name: 'PP1Search',

  data() {
    return {
      showFixedSearch: false,
      showFixedBottom: false,
      searchBox: "",
      bloglist: []
    };
  },

  mounted() {
    this.showBlogs(); // 监听页面滚动事件

    window.addEventListener("scroll", this.showSearch);
  },

  methods: {
    showSearch() {
      // 获取当前滚动条向下滚动的距离
      let scrollTop1 = document.documentElement.scrollTop || document.body.scrollTop; // 当页面滚动到高度160px处时，显示搜索框

      if (scrollTop1 > 160) {
        this.showFixedSearch = true;
      } else {
        this.showFixedSearch = false;
      }
    },

    Search() {
      if (this.searchBox == "") {
        alert("Please check your input");
      } else {
        this.$router.push({
          path: `/PP1Search/PP1Result/${this.searchBox}`
        });
      }
    },

    showBlogs() {
      axios.post('http://127.0.0.1:8000/json/home/', {
        post_type: "Student Request"
      }).then(response => {
        console.log(response.data);

        if (response.data['status'] != 1) {
          alert('Sorry but you are not login');
          this.$router.push('/');
        } else {
          this.bloglist = response.data['content'];
        }
      });
    }

  }
};
